const rawText = $input.first().json.text

// Remove code block markers (```json ... ```) and whitespace
const cleaned = rawText
  .replace(/```json|```/g, '')  // remove triple backticks
  .trim();

let hashtags = [];

try {
  hashtags = JSON.parse(cleaned);
} catch (error) {
  throw new Error("Failed to parse hashtags: " + error.message);
}

return [
  {
    json: {
      hashtags
    }
  }
];

// Get the 'data' array from the input
const profiles = items[0].json.data;

// Use a Set to track unique profile URLs
const seen = new Set();

// Filter and map to keep only unique profile URLs
const uniqueUrls = profiles
  .map(item => item.authorMeta?.profileUrl)
  .filter(url => {
    if (!url || seen.has(url)) return false;
    seen.add(url);
    return true;
  });

// Return as n8n items
return uniqueUrls.map(url => ({
  json: { profileUrl: url }
}));

