// Input: search response with an "items" array
const items = $input.item.json.items;

// Extract all channelIds from the search results
const channelIds = items
  .filter(i => i.id?.kind === 'youtube#channel')
  .map(i => i.id.channelId);

// Join them into a comma-separated string
const idString = channelIds.join(',');

// Output the formatted ID string
return [
  {
    json: {
      channelIdsCsv: idString
    }
  }
];

const input = items[0].json.items; // This is the array of channels

const output = input.map(channel => {
  const snippet = channel.snippet;
  const stats = channel.statistics;

  // Convert publishedAt to profile age in years
  const publishedDate = new Date(snippet.publishedAt);
  const now = new Date();
  const ageInMs = now - publishedDate;
  const ageInYears = ageInMs / (1000 * 60 * 60 * 24 * 365.25);
  const profileAge = `${ageInYears.toFixed(1)} years`;

  return {
    json: {
      channelId: channel.id,
      name: snippet.title,
      description: snippet.description,
      customUrl: snippet.customUrl || "",
      country: snippet.country || "",
      profileAge: profileAge,
      subscriberCount: parseInt(stats.subscriberCount || "0"),
      viewCount: parseInt(stats.viewCount || "0"),
      videoCount: parseInt(stats.videoCount || "0"),
      thumbnail: snippet.thumbnails?.high?.url || "",
    }
  };
});

return output;

