const input = item.json;

const name = input.user?.uniqueId || '';
const nickName = input.user?.nickname || '';
const signature = input.user?.signature || '';
const bioLink = input.user?.bioLink || '';  // Still might be empty
const fans = input.stats?.followerCount || 0;
const follows = input.stats?.followingCount || 0;
const video = input.stats?.videoCount || 0;

const timestamp = input.user?.createTime;
let profileAge = 'Unknown';

if (typeof timestamp === 'number') {
  const createdDate = new Date(timestamp * 1000);
  const now = new Date();
  const diff = now - createdDate;
  const days = Math.floor(diff / (1000 * 60 * 60 * 24));
  profileAge = `${days} days`;
}

return {
  json: {
    name,
    nickName,
    signature,
    bioLink,
    fans,
    follows,
    video,
    profileAge
  }
};

